import React, { useEffect, useState } from 'react'
import DataTable from 'react-data-table-component'
import axios from 'axios'
import del from './Delete.png'
import edit from './penciledit.png'
import './App.css'
import {Link} from 'react-router-dom'

const DataTable1 = () => {
    const [allData, setAllData] = useState([]);
    const [flag, setFlag] = useState(1);
    const [name, setName] = useState("");
    const [email, setEmail] = useState("");
    const [address, setAddress] = useState("");
    const [phone, setPhone] = useState("");
    const [id, setId] = useState('');
    const columns = [
        {
            name:"Name",
            selector: row => row.name,
            sortable:true
        },
        {
            name:"Email",
            selector: row => row.email
        },
        {
            name:"Address",
            selector: row => row.address
        },
        {
            name:"Phone",
            selector: row => row.phone
        },
        {
            name:"Actions",
            selector: row => <><img width={20} height={20} src={edit} alt="edit" /> 
            <img width={20} height={20} src={del} alt="delete" /></>
        }
    ]
    const handleSelected = ({selectedRows})=>{
        const selectedIds = selectedRows.map(row => row._id);
        setId(selectedIds);
    }
    

    const handleDelete = () => {
        if (id.length === 0) {
            alert('Please select at least one item to delete.');
            return;
        }
    
        if (window.confirm('Are you sure you want to delete selected item(s)?')) {
            id.forEach(itemId => {
                axios.delete(`http://localhost:8000/del/${itemId}`)
                    .then(result => {
                        console.log(result);
                    })
                    .catch(err => console.log(err));
            });
    
            setFlag(1);
        }
    };
    
    
    const showAddData = () =>{
        
        setFlag(2)
        console.log(flag)
    }
   const showDelete = () =>{
    setFlag(3)
    console.log(flag)
   }

   

   const addDetails = () => {
    if (!name || !email || !phone || !address) {
        alert('Please fill in all the required fields.');
        return;
    }

    axios.post('http://localhost:8000/add', { name, email, address, phone })
        .then(result => {
            console.log(result);
            setName('');
            setEmail('');
            setAddress('');
            setPhone('');
            
        })
        .catch(err => console.log(err))
        .finally(() => {
            setFlag(1); // Set flag to refresh data table after adding details
        });
};


    useEffect(()=>{
        axios.get('http://localhost:8000/list')
              .then(result => setAllData(result.data))
              .catch(err=>console.log(err))
        
              
      },[flag])
  return (
    <div>
            <div className='assignment'>
                <h2>Assignment 1</h2>
            </div>
            <div className='assignment'>
        <Link to='/assignment2'><h2>Assignment 2</h2></Link>
            </div>
        
      { flag === 1 ?
    <DataTable
        
        columns={columns}
        data={allData}
        pagination
        fixedHeader
        selectableRows
        selectableRowsHighlight
        onSelectedRowsChange={handleSelected}
        subHeader
        subHeaderComponent={
           <>
           <h3 style={{color:'black',textAlign:'left',paddingRight:'750px',}}><b>Manage Employees</b></h3>
            <button className='button1' onClick={showDelete}>Delete</button>
            <button className='button2' onClick={showAddData}>Add New Employee</button>
            </>
        }
    />   
        : flag === 2 ?
    <div className='edit'>
        <h3>Add Employee Details</h3>
        <b>Name: </b>
        <input type="text" name='name' onChange={(e)=>setName(e.target.value)}/> <br></br>

        <b>Email:  </b>
        <input type="text" name='email' onChange={(e)=>setEmail(e.target.value)}/> <br></br>

        <b>Phone:</b>
        <input type="text" name='phone' onChange={(e)=>setPhone(e.target.value)}/> <br></br>

        <b>Address:</b>
        <textarea name="address" id="" cols="23" rows="2" onChange={(e)=>setAddress(e.target.value)}></textarea>
        <br></br>

        

        <button className='button2' onClick={addDetails}>Add Details</button> || <button className='button1' onClick={()=>setFlag(1)}>Cancel</button>

    </div>
    :
    <div className='del'>
        <h4>Delete Employees</h4>
        <h5>Are You sure you want to delete these records?</h5>
        <h6>This action cannot be undone</h6>
        <button className='button3' onClick={()=>setFlag(1)}>Cancel</button>  
        <button className='button1' onClick={handleDelete}>Delete</button>
    </div>
}        
    </div>
  )
}

export default DataTable1
