import React, { useEffect, useState } from 'react';
import DataTable from 'react-data-table-component';
import axios from 'axios';
import del from './Delete.png';
import edit from './penciledit.png';
import './App.css';

const DataTable1 = () => {
    const [allData, setAllData] = useState([]);
    const [flag, setFlag] = useState(1);
    const [name, setName] = useState("");
    const [email, setEmail] = useState("");
    const [address, setAddress] = useState("");
    const [phone, setPhone] = useState("");
    const [id, setId] = useState('');
    const [editId, setEditId] = useState(null);

    const columns = [
        {
            name: <b>Name</b>,
            selector: row => row.name,
            sortable: true
        },
        {
            name: <b>Email</b>,
            selector: row => row.email
        },
        {
            name: <b>Address</b>,
            selector: row => row.address
        },
        {
            name: <b>Phone</b>,
            selector: row => row.phone
        },
        {
            name: <b>Actions</b>,
            cell: row => (
                <div>
                    <img
                        src={edit}
                        alt="edit"
                        width={20} height={20}
                        style={{ cursor: 'pointer', marginRight: '10px' }}
                        onClick={() => handleEdit(row)}
                    />
                    <img
                        src={del}
                        alt="delete"
                        width={20} height={20}
                        style={{ cursor: 'pointer' }}
                        onClick={() => handleDelete(row)}
                    />
                </div>
            ),
            ignoreRowClick: true,
            allowOverflow: true,
            button: true,
        }
    ];

    const handleSelected = ({ selectedRows }) => {
        const selectedIds = selectedRows.map(row => row._id);
        setId(selectedIds);
    };

    const handleEdit = (row) => {
        const { _id, name, email, address, phone } = row;
        setName(name);
        setEmail(email);
        setAddress(address);
        setPhone(phone);
        setEditId(_id);
        setFlag(2);
    };

    const handleDelete = (row) => {
        const { _id } = row;

        if (window.confirm('Are you sure you want to delete this item?')) {
            axios.delete(`http://localhost:8000/del/${_id}`)
                .then(result => {
                    console.log(result);
                    setFlag(prevFlag => prevFlag + 1); // Force re-fetching data
                })
                .catch(err => console.log(err));
        }
    };

    const showAddData = () => {
        setName("");
        setEmail("");
        setAddress("");
        setPhone("");
        setEditId(null);
        setFlag(2);
    };

    const addDetails = (e) => {
        e.preventDefault();
        if (!name || !email || !phone || !address) {
            alert('Please fill in all the required fields.');
            return;
        }

        if (editId) {
            axios.put(`http://localhost:8000/edit/${editId}`, { name, email, address, phone })
                .then(result => {
                    console.log(result.data);
                    setFlag(1);
                    alert("Record updated successfully!");
                })
                .catch(err => {
                    console.error("Error updating record:", err);
                    alert("Error updating record. Please try again later.");
                });
        } else {
            axios.post('http://localhost:8000/add', { name, email, address, phone })
                .then(result => {
                    console.log(result);
                    setName('');
                    setEmail('');
                    setAddress('');
                    setPhone('');
                    setFlag(1);
                })
                .catch(err => console.log(err));
        }
    };

    useEffect(() => {
        axios.get('http://localhost:8000/list')
            .then(result => setAllData(result.data))
            .catch(err => console.log(err));
    }, [flag]);

    return (
        <div>
            {flag === 1 ? (
                <DataTable
                    columns={columns}
                    data={allData}
                    pagination
                    fixedHeader
                    selectableRows
                    selectableRowsHighlight
                    onSelectedRowsChange={handleSelected}
                    title={<b>Employee CRUD Operations:</b>}
                    subHeader
                    subHeaderComponent={
                        <div style={{ backgroundColor: '#28466C', padding: '10px', display: 'flex', alignItems: 'center' }}>
                            <h3 style={{ color: 'white', margin: '0', padding: '10px', textAlign: 'left' }}><b>Manage Employees</b></h3>
                            <button className='button1' onClick={showAddData}>Add New Employee</button>
                        </div>
                    }
                />
            ) : (
                <div className="container">
                    <form className='edit' onSubmit={addDetails}>
                        <h3>{editId ? "Edit Employee Details" : "Add Employee Details"}</h3>
                        <label><b>Name:</b></label>
                        <input type="text" name='name' value={name} onChange={(e) => setName(e.target.value)} /> <br></br>
                        <label><b>Email:</b></label>
                        <input type="text" name='email' value={email} onChange={(e) => setEmail(e.target.value)} /> <br></br>
                        <label><b>Phone:</b></label>
                        <input type="text" name='phone' value={phone} onChange={(e) => setPhone(e.target.value)} /> <br></br>
                        <label><b>Address:</b></label>
                        <textarea name="address" id="" cols="23" rows="2" value={address} onChange={(e) => setAddress(e.target.value)}></textarea>
                        <br></br>
                        <div className="button-container">
                            <button className='button1' onClick={() => setFlag(1)}>Cancel</button>
                            <button className='button2' type="submit">{editId ? "Update Details" : "Add Details"}</button>
                        </div>
                    </form>
                </div>
            )}
        </div>
    );
};

export default DataTable1;
