import React, { useEffect, useState } from 'react'
import DataTable from 'react-data-table-component'
import axios from 'axios'
import del from './Delete.png'
import edit from './penciledit.png'
import './App.css'


const DataTable1 = () => {
    const [allData, setAllData] = useState([]);
    const [flag, setFlag] = useState(1);
    const [name, setName] = useState("");
    const [email, setEmail] = useState("");
    const [address, setAddress] = useState("");
    const [phone, setPhone] = useState("");
    const [id, setId] = useState('');
    const columns = [
        {
            name:<b>Name</b>,
            selector: row => row.name,
            sortable:true
        },
        {
            name:<b>Email</b>,
            selector: row => row.email
        },
        {
            name:<b>Address</b>,
            selector: row => row.address
        },
        {
            name:<b>Phone</b>,
            selector: row => row.phone
        },
        {
            name: <b>Actions</b>,
            cell: row => (
                <div>
                    <img
                        src={edit}
                        alt="edit"
                        width={20} height={20}
                        style={{ cursor: 'pointer', marginRight: '10px' }}
                        onClick={() => handleEdit(row)}
                    />
                    <img
                        src={del}
                        alt="delete"
                        width={20} height={20}
                        style={{ cursor: 'pointer' }}
                        onClick={() => handleDelete(row)}
                    />
                </div>
            ),
            ignoreRowClick: true,
            allowOverflow: true,
            button: true,
        }

    ]
    const handleSelected = ({selectedRows})=>{
        const selectedIds = selectedRows.map(row => row._id);
        setId(selectedIds);
    }

    const handleEdit = (row) => {
        const { _id, name, email, address, phone } = row;

        const newName = prompt("Enter new name:", name);
        const newEmail = prompt("Enter new email:", email);
        const newAddress = prompt("Enter new address:", address);
        const newPhone = prompt("Enter new phone:", phone);

        if (!newName || !newEmail || !newAddress || !newPhone) {
            alert("Please fill in all fields to update.");
            return;
        }

        axios.put(`http://localhost:8000/edit/${_id}/${newName}/${newEmail}/${newAddress}/${newPhone}`)
            .then(result => {
                console.log(result.data);
                setFlag(flag + 1); 
                alert("Record updated successfully!");
            })
            .catch(err => {
                console.error("Error updating record:", err);
                alert("Error updating record. Please try again later.");
            });
    };


    const handleDelete = () => {
        if (id.length === 0) {
            alert('Please select at least one item to delete.');
            return;
        }

        if (window.confirm('Are you sure you want to delete selected item(s)?')) {
            id.forEach(itemId => {
                axios.delete(`http://localhost:8000/del/${itemId}`)
                    .then(result => {
                        console.log(result);
                    })
                    .catch(err => console.log(err));
            });

            setFlag(1);
        }
    };


    const showAddData = () =>{

        setFlag(2)
        console.log(flag)
    }
   const showDelete = () =>{
    setFlag(3)
    console.log(flag)
   }



   const addDetails = () => {
    if (!name || !email || !phone || !address) {
        alert('Please fill in all the required fields.');
        return;
    }

    axios.post('http://localhost:8000/add', { name, email, address, phone })
        .then(result => {
            console.log(result);
            setName('');
            setEmail('');
            setAddress('');
            setPhone('');

        })
        .catch(err => console.log(err))
        .finally(() => {
            setFlag(1);
        });
};


    useEffect(()=>{
        axios.get('http://localhost:8000/list')
              .then(result => setAllData(result.data))
              .catch(err=>console.log(err))


      },[flag])
  return (
    <div>


      { flag === 1 ?
    <DataTable

        columns={columns}
        data={allData}
        pagination
        fixedHeader
        selectableRows
        selectableRowsHighlight
        onSelectedRowsChange={handleSelected}
        title={<b>Employee CRUD Operations:</b>}
        subHeader
        subHeaderComponent={
            <div style={{ backgroundColor: '#28466C',padding: '10px', display: 'flex', alignItems: 'center', }}>
            <h3 style={{ color: 'white', margin: '0',padding: '10px',textAlign: 'left', }}><b>Manage Employees</b></h3>

                <button className='button1' onClick={showDelete}>Delete Al</button>
                <button className='button2' onClick={showAddData}>Add New Employee</button>
        </div>
        }
    />   
        : flag === 2 ?
        <div className="container">
    <form className='edit'>
        <h3>Add Employee Details</h3>
        <label><b>Name:</b></label>
        <input type="text" name='name' onChange={(e) => setName(e.target.value)} /> <br></br>

        <label><b>Email:</b></label>
        <input type="text" name='email' onChange={(e) => setEmail(e.target.value)} /> <br></br>

        <label><b>Phone:</b></label>
        <input type="text" name='phone' onChange={(e) => setPhone(e.target.value)} /> <br></br>

        <label><b>Address:</b></label>
        <textarea name="address" id="" cols="23" rows="2" onChange={(e) => setAddress(e.target.value)}></textarea>
        <br></br>
        <div className="button-container">
            <button className='button1' onClick={() => setFlag(1)}>Cancel</button>
            <button className='button2' onClick={addDetails}>Add Details</button>
        </div>
    </form>
</div>



    :
    <div className='del'>
        <h4>Delete Employees</h4>
        <h5>Are You sure you want to delete these records?</h5>
        <h6>This action cannot be undone</h6>
        <button className='button3' onClick={()=>setFlag(1)}>Cancel</button>  
        <button className='button1' onClick={handleDelete}>Delete</button>
    </div>
}        
    </div>
  )
}

export default DataTable1
