        {
            name:"Name",
            selector: row => row.name,
            sortable:true
        },
        {
            name:"Email",
            selector: row => row.email
        },
        {
            name:"Address",
            selector: row => row.address
        },
        {
            name:"Phone",
            selector: row => row.phone
        },
        {
            name:"Actions",
            selector: row => <><img width={20} height={20} src={edit} alt="edit" /> <img width={20} height={20} src={del} alt="delete" /></>
        }
